const signUpButton=document.querySelector("#signup-btn"),logInOptions=document.querySelector(".is-hidden"),p=document.querySelector(".p"),welcome=document.querySelector(".welcome");let signUpButtonAria=document.querySelector("#signup-btn").getAttribute("aria-expanded");const emailReveal=document.querySelector(".email-reveal"),emailHide=document.querySelector(".email-hide"),articleHide=document.querySelector(".a");function toggleAriaSignUp(){return signUpButtonAria="true"==signUpButtonAria?"false":"true"}document.addEventListener("DOMContentLoaded",()=>{signUpButton.addEventListener("click",function(){p.classList.toggle("no-p"),welcome.classList.toggle("no-p"),logInOptions.classList.toggle("is-seen"),signUpButton.setAttribute("aria-expanded",toggleAriaSignUp())}),emailReveal.addEventListener("click",()=>{emailHide.classList.toggle("email-show"),articleHide.classList.toggle("will-hide")})}),document.querySelector("#btnSignup").addEventListener("click",()=>{const e=document.querySelector("#txtEmail").value,t=document.querySelector("#txtPassword").value;firebase.auth().createUserWithEmailAndPassword(e,t).catch(function(e){console.log(e.message)})}),document.querySelector("#btnLogin").addEventListener("click",()=>{const e=document.querySelector("#txtEmail").value,t=document.querySelector("#txtPassword").value;firebase.auth().signInWithEmailAndPassword(e,t).catch(function(e){console.log(e.message)})}),document.querySelector(".fb-login").addEventListener("click",()=>{const e=new firebase.auth.FacebookAuthProvider;firebase.auth().signInWithPopup(e).then(e=>console.log(e)).catch(e=>console.log(e))}),firebase.auth().onAuthStateChanged(e=>e&&(window.location="product.html"));